"10.438" transform="rotate(-180 484.301 432.112)" fill="#1E293B" stroke="#334155"></circle><circle cx="584.5" cy="432.112" r="10.438" transform="rotate(-180 584.5 432.112)" fill="#1E293B" stroke="#334155"></circle><circle cx="584.5" cy="642.95" r="10.438" transform="rotate(-180 584.5 642.95)" fill="#1E293B" stroke="#334155"></circle><circle cx="484.301" cy="851.699" r="10.438" transform="rotate(-180 484.301 851.699)" stroke="#334155"></circle><circle cx="384.1" cy="256.763" r="10.438" transform="rotate(-180 384.1 256.763)" stroke="#334155"></circle></g></svg>
        <q-card
          v-bind:style="q.screen.lt.sm ? { width: '80%' } : { width: '30%', minWidth: '350px', maxWidth: '400px'}"
        >
          <q-card-section class="q-mb-md">
            <q-avatar
              size="150px"
              class="absolute-center shadow-10"
              color="primary"
            >
              <img src="~assets/nostrich-head-32.svg" class="q-pa-md" />
            </q-avatar>
          </q-card-section>
          <q-card-section>
            <div class="text-center q-mt-lg q-pt-lg">
              <div v-if="isSignupRequest" class="col text-h6 ellipsis">
                Register
              </div>
              <div v-else class="col text-h6 ellipsis">Login</div>
            </div>
            <div class="text-center q-pt-lg">
              <div class="col ellipsis">
                Manage your <strong>Nostr</strong> accounts.
              </div>
            </div>
          </q-card-section>
          <q-card-section>
            <q-form @submit="onSubmit">
              <q-input
                filled
                v-model="username"
                :rules="[checkUsername]"
                label="Username"
                lazy-rules
              />

              <q-input
                type="password"
                filled
                v-model="password"
                :rules="[checkPassword]"
                label="Password"
                lazy-rules
              />

              <q-input
                v-if="isSignupRequest"
                type="password"
                filled
                v-model="passwordRepeat"
                :rules="[checkPassword]"
                label="Confirm password"
                lazy-rules
              />
              <q-linear-progress
                v-if="inProgress"
                indeterminate
                color="secondary"
                class="q-mt-sm"
              />

              <q-btn
                v-if="!this.isSignupRequest"
                label="Login"
                @click="login"
                type="submit"
                color="primary"
                class="full-width"
                :disable="inProgress"
              />

              <div v-if="!this.isSignupRequest" class="q-mt-sm text-center">
                <span>or</span>
              </div>

              <q-btn
                label="Register"
                @click="signup"
                type="submit"
                color="secondary"
                class="full-width q-mt-sm"
                :disable="inProgress"
              />
              <q-btn
                v-if="this.isSignupRequest"
                @click="this.isSignupRequest = false"
                label="Back"
                type="button"
                class="full-width q-mt-md"
                color="grey"
              />
            </q-form>
          </q-card-section>
