  </q-layout>
</template>

<script>
import { defineComponent } from "vue";
import { ref } from "vue";
import { useQuasar } from "quasar";

import { saas } from "boot/saas";

export default defineComponent({
  setup() {
    const $q = useQuasar();
    return {
      q: $q,
      username: ref(""),
      password: ref(""),
      passwordRepeat: ref(""),
      isSignupRequest: ref(false),
      inProgress: ref(false),
    };
  },

  methods: {
    async login() {
      try {
        this.inProgress = true;
        const message = this.validateForm();
        if (message) {
          this.q.notify({
            message,
            color: "negative",
            icon: "warning",
          });
          return false;
        }
        await saas.login(this.username, this.password);
        this.q.notify({
          message: "Logged in!",
          color: "positive",
        });
        setTimeout(() => (window.location.href = "/"), 500);
      } catch (error) {
        console.warn(error);
        this.q.notify({
          message: "Failed to login!",
          caption: saas.mapErrorToString(error),
          color: "negative",
          icon: "warning",
        });
        return false;
      } finally {
        this.inProgress = false;
      }
    },
    checkUsername(val) {
      return (
        (val && val.length >= 3) || "Username must have at least 3 characters"
      );
    },
    checkPassword(val) {
      return (
        (val && val.length >= 8) || "Password must have at least 8 characters"
      );
    },
    validateForm() {
      const usernameMessage = this.checkUsername(this.username);
      if (usernameMessage !== true) {
        return usernameMessage;
      }
      const passwordMessage = this.checkPassword(this.password);
      if (passwordMessage !== true) {
        return passwordMessage;
      }
      return null;
    },
    validateSignupForm() {
      const message = this.validateForm();
      if (message) {
        return message;
      }
      const passwordRepeatMessage = this.checkPassword(this.passwordRepeat);
      if (passwordRepeatMessage !== true) {
        return passwordRepeatMessage;
      }
      if (this.password !== this.passwordRepeat) {
        return "Passwords do not match!";
      }

      return null;
    },

    async onSubmit() {
      if (this.isSignupRequest) {
        await this.signup();
      } else {
        await this.login();
      }
    },
    async signup() {
      if (!this.isSignupRequest) {
        this.isSignupRequest = true;
        return;
      }
      const message = this.validateSignupForm();
      if (message) {
        this.q.notify({
          message,
          color: "negative",
          icon: "warning",
        });
        return;
      }

      try {
        this.inProgress = true;
        await saas.signup(this.username, this.password, this.passwordRepeat);
        this.q.notify({
          message: "Signed Up!",
          color: "positive",
        });
        setTimeout(() => (window.location.href = "/"), 500);
      } catch (error) {
        console.warn(error);
        this.q.notify({
          message: "Failed to register!",
          caption: saas.mapErrorToString(error),
          color: "negative",
          icon: "warning",
        });
        return false;
      } finally {
        this.inProgress = false;
      }
    },
  },
});
</script>

